mapper.select:
select reportNo ,userId ,reportRegDate ,reportType,uniqueNo,processState
from report where uniqueNo = #{param1} and reportType="글" and  processState=#{param2}
order by reportRegDate desc LIMIT 10 OFFSET #{param3}

