mapper.insert:
INSERT INTO groupBuyingPost (userId,categoryNo,gbContent,title,recruitPeople,finishDate,startDate,joinPrice,gbState,blockState) 
VALUES (#{userId},(
SELECT categoryNo
FROM category
WHERE categoryType = #{categoryType}
),#{gbContent},#{title},#{recruitPeople},#{finishDate},#{startDate},#{joinPrice},#{gbState},'N');

mapper.select:
SELECT newName
FROM groupbuyingimage 
WHERE gbNo = #{param1}

mapper.update:
UPDATE groupBuyingPost
SET gbState=#{param2}
WHERE gbNo=#{param1};

mapper.insert:
insert into report (userId, reportContent, reportRegDate, reportType, uniqueNo, processState)

values (#{userId}, #{reportContent}, curdate(), '글', #{gbNo}, '미처리');


values (#{userId}, #{reportContent}, curdate(), '댓글', #{commNo}, '미처리');

mapper.select:
SELECT gb.gbNo, gb.recruitPeople, gb.title, gb.finishDate, gb.startDate, gb.joinPrice, c.categoryType, gb.userId, (SELECT nickname FROM user u WHERE u.userid = gb.userId )nickname, (SELECT count(userId) FROM groupBuyingJoining WHERE groupBuyingJoining.gbNo = gb.gbNo) joinPeople, (SELECT g2.newName FROM groupbuyingimage g2 WHERE g2.gbNo = gb.gbNo ORDER BY g2.imgNo LIMIT 1) newName FROM groupBuyingPost gb LEFT OUTER join groupbuyingregions loc ON gb.gbNo = loc.gbNo LEFT OUTER join category c ON gb.categoryNo=c.categoryNo WHERE gb.blockState = 'N' AND gb.gbState like '진행'
AND c.categoryNo=#{category}
gb.title LIKE CONCAT('%',#{keyWord},'%')
AND c.categoryNo=#{category} AND gb.title LIKE CONCAT('%',#{keyWord},'%')
AND loc.gbDetailAddress=#{region}
AND loc.gbDetailAddress=#{region} AND c.categoryNo=#{category}
AND loc.gbDetailAddress=#{region} AND gb.title LIKE CONCAT('%',#{keyWord},'%')
AND loc.gbDetailAddress=#{region} AND c.categoryNo=#{category} AND gb.title LIKE CONCAT('%',#{keyWord},'%')
LIMIT 8 OFFSET #{offset}

SELECT count(gb.gbNo) totalCnt FROM groupBuyingPost gb LEFT OUTER join groupbuyingregions loc ON gb.gbNo = loc.gbNo LEFT OUTER join category c ON gb.categoryNo=c.categoryNo WHERE gb.blockState = 'N' AND gb.gbState like '진행'
AND c.categoryNo=#{category}
gb.title LIKE CONCAT('%',#{keyWord},'%')
AND c.categoryNo=#{category} AND gb.title LIKE CONCAT('%',#{keyWord},'%')
AND loc.gbDetailAddress=#{region}
AND loc.gbDetailAddress=#{region} AND c.categoryNo=#{category}
AND loc.gbDetailAddress=#{region} AND gb.title LIKE CONCAT('%',#{keyWord},'%')
AND loc.gbDetailAddress=#{region} AND c.categoryNo=#{category} AND gb.title LIKE CONCAT('%',#{keyWord},'%')
